import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) return alert("Please login");

    axios.get(`http://localhost:5000/api/orders/user/${user._id}`, {
      headers: { Authorization: `Bearer ${user.token}` }
    })
      .then(res => setOrders(res.data))
      .catch(err => console.error("Order fetch failed", err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-center mb-6">ðŸ“¦ My Orders</h1>
      {loading ? (
        <p>Loading orders...</p>
      ) : orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order) => (
          <div key={order._id} className="border p-4 rounded mb-4 bg-white shadow">
            <h2 className="text-lg font-semibold">Order ID: {order._id}</h2>
            <p>Placed on: {new Date(order.createdAt).toLocaleString()}</p>
            <p>Shipping to: {order.name}, {order.address}</p>
            <p>Phone: {order.phone}</p>
            <ul className="mt-2 list-disc ml-5">
              {order.items.map((item, i) => (
                <li key={i}>{item.name} - â‚¹{item.price} Ã— {item.quantity}</li>
              ))}
            </ul>
            <p className="font-bold mt-2">Total: â‚¹{order.total}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default Orders;
