import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem('user'));

    if (!user) {
      alert("Please login to place an order.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/orders', {
        userId: user._id,
        name,
        address,
        phone,
        items: cart,
        total,
      }, {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      });

      if (response.status === 201 || response.status === 200) {
        alert("‚úÖ Order placed successfully!");
        clearCart();
        navigate('/thankyou');
      } else {
        alert("‚ùå Failed to place order.");
      }
    } catch (error) {
      console.error("Order Error:", error);
      alert("‚ùå Server error while placing order.");
    }
  };

  return (
    <div className="p-6 bg-gradient-to-br from-purple-50 to-blue-50 min-h-screen">
      <h1 className="text-4xl font-bold text-center text-purple-700 mb-8">üõí Checkout</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-5xl mx-auto">
        {/* üßæ Order Summary */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">Order Summary</h2>
          {cart.length === 0 ? (
            <p className="text-gray-500">Your cart is empty.</p>
          ) : (
            <ul className="space-y-3">
              {cart.map((item, index) => (
                <li key={index} className="border-b pb-2">
                  <div className="font-medium">{item.name}</div>
                  <div className="text-sm text-gray-600">Qty: {item.quantity} √ó ‚Çπ{item.price}</div>
                </li>
              ))}
              <li className="font-bold text-lg pt-4 text-blue-800">Total: ‚Çπ{total}</li>
            </ul>
          )}
        </div>

        {/* üìù Checkout Form */}
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md space-y-4">
          <h2 className="text-2xl font-semibold mb-2 text-gray-800">Shipping Details</h2>

          <input
            type="text"
            placeholder="Full Name"
            className="border w-full p-2 rounded"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />

          <input
            type="tel"
            placeholder="Phone Number"
            className="border w-full p-2 rounded"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            pattern="[0-9]{10}"
            title="Enter 10-digit phone number"
            required
          />

          <textarea
            placeholder="Full Address"
            className="border w-full p-2 rounded"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />

          <button
            type="submit"
            className="w-full bg-purple-600 hover:bg-purple-700 text-white py-2 rounded font-semibold"
          >
            ‚úÖ Place Order
          </button>
        </form>
      </div>
    </div>
  );
};

export default Checkout;
