import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/orders')
      .then(res => setOrders(res.data))
      .catch(err => console.error("Failed to fetch orders", err));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">All Orders</h2>
      <ul className="space-y-2">
        {orders.map(order => (
          <li key={order._id} className="bg-white p-4 shadow rounded">
            Order by User ID: {order.userId} - Product ID: {order.productId} - Date: {new Date(order.date).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminOrders;
