import React from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen relative bg-gradient-to-bl from-[#0f0c29] via-[#302b63] to-[#24243e] font-[Orbitron] overflow-hidden">
      {/* Background Video */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute top-0 left-0 w-full h-full object-cover z-[-1] opacity-20"
      >
        <source src="/video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Overlay */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-6 text-center backdrop-blur-sm">
        <h1 className="text-5xl text-white font-bold mb-12 animate-pulse drop-shadow-[0_0_10px_rgba(255,255,255,0.5)]">
          âš¡ Control Center
        </h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-xl">
          {[
            { name: "Users", route: "/users", color: "from-cyan-400 to-blue-600" },
            { name: "Products", route: "/products", color: "from-pink-500 to-red-600" },
            { name: "Orders", route: "/orders", color: "from-yellow-400 to-orange-500" },
            { name: "Admin", route: "/admin-login", color: "from-purple-500 to-indigo-600" },
          ].map(({ name, route, color }) => (
            <button
              key={name}
              onClick={() => navigate(route)}
              className={`p-6 bg-gradient-to-tr ${color} text-white text-xl rounded-xl shadow-lg transform transition hover:scale-105 hover:shadow-[0_0_20px_rgba(255,255,255,0.5)]`}
            >
              {name}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
