import React, { useEffect, useState } from 'react';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const cartData = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(cartData);
  }, []);

  const removeFromCart = (productId) => {
    const updatedCart = cartItems.filter(item => item._id !== productId);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    setCartItems(updatedCart);
  };

  const getTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price, 0);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-4xl font-bold text-center mb-6">üõí Your Cart</h1>

      {cartItems.length === 0 ? (
        <p className="text-center text-lg text-gray-700">Your cart is empty.</p>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {cartItems.map((item) => (
              <div key={item._id} className="bg-white rounded-xl shadow-md p-4">
                <img src={item.image} alt={item.name} className="h-48 w-full object-cover rounded" />
                <h2 className="text-xl font-semibold mt-2">{item.name}</h2>
                <p className="text-green-600 font-medium">‚Çπ{item.price}</p>
                <p className="text-yellow-500">‚≠ê {item.rating}</p>
                <button
                  onClick={() => removeFromCart(item._id)}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700"
                >
                  Remove
                </button>
              </div>
            ))}
          </div>

          <div className="mt-8 text-center">
            <h2 className="text-2xl font-bold text-gray-800">Total: ‚Çπ{getTotal()}</h2>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
