import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [expandedProductId, setExpandedProductId] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    address: '',
    date: ''
  });

  useEffect(() => {
    const cartData = JSON.parse(localStorage.getItem('cart')) || [];
    // Ensure each item has a quantity (default 1)
    const updated = cartData.map(item => ({ ...item, quantity: item.quantity || 1 }));
    setCartItems(updated);
  }, []);

  const removeFromCart = (productId) => {
    const updated = cartItems.filter(item => item._id !== productId);
    localStorage.setItem('cart', JSON.stringify(updated));
    setCartItems(updated);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleQuantityChange = (id, qty) => {
    const updated = cartItems.map(item =>
      item._id === id ? { ...item, quantity: parseInt(qty) } : item
    );
    setCartItems(updated);
    localStorage.setItem('cart', JSON.stringify(updated));
  };

  const placeOrder = async (e, product) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user._id) {
      alert("You must be logged in to place an order.");
      return;
    }

    const { name, phone, address, date } = formData;
    if (!name || !phone || !address || !date) {
      alert("Please fill out all fields including date.");
      return;
    }

    const orderPayload = {
      userId: user._id,
      name,
      phone,
      address,
      items: [{
        productId: product._id,
        name: product.name,
        price: product.price,
        quantity: product.quantity
      }],
      total: product.price * product.quantity,
      createdAt: new Date(date).toISOString()
    };

    try {
      await axios.post('http://localhost:5000/api/orders', orderPayload);
      alert(`✅ Order placed for ${product.name}`);
      setFormData({ name: '', phone: '', address: '', date: '' });
      setExpandedProductId(null);
    } catch (err) {
      console.error('❌ Order failed:', err);
      alert('❌ Failed to place order. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-pink-200 to-yellow-100 p-6">
      <div className="text-center mb-8">
        <h1 className="text-5xl font-extrabold text-pink-800 tracking-tight animate-pulse mb-2">Your Shopping Cart</h1>
        <p className="text-lg text-gray-700 italic">Choose quantity and date before placing order.</p>
      </div>

      {cartItems.length === 0 ? (
        <p className="text-center text-lg text-gray-700 mt-10">Your cart is empty. Start shopping now!</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {cartItems.map((item) => (
            <div key={item._id} className="bg-white rounded-2xl shadow-xl p-5 transform hover:scale-105 transition duration-300 ease-in-out">
              <img src={item.image} alt={item.name} className="h-48 w-full object-cover rounded-xl shadow-sm" />
              <h2 className="text-xl font-bold mt-4 text-gray-800">{item.name}</h2>
              <p className="text-green-600 font-semibold mt-1">₹{item.price}</p>
              <p className="text-yellow-500 text-sm">⭐ {item.rating} / 5</p>
              <div className="mt-2">
                <label className="block text-sm font-medium text-gray-700 mb-1">Quantity:</label>
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => handleQuantityChange(item._id, e.target.value)}
                  className="w-full px-3 py-1 border rounded-md"
                />
              </div>
              <div className="mt-4 space-y-2">
                <button
                  onClick={() => setExpandedProductId(item._id)}
                  className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded-xl"
                >
                  Order This Product
                </button>
                <button
                  onClick={() => removeFromCart(item._id)}
                  className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded-xl"
                >
                  Remove
                </button>
              </div>

              {expandedProductId === item._id && (
                <form
                  onSubmit={(e) => placeOrder(e, item)}
                  className="mt-6 bg-gray-100 p-4 rounded-xl space-y-3"
                >
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleFormChange}
                    placeholder="Your Name"
                    className="w-full px-3 py-2 border rounded-md"
                  />
                  <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleFormChange}
                    placeholder="Phone Number"
                    className="w-full px-3 py-2 border rounded-md"
                  />
                  <textarea
                    name="address"
                    value={formData.address}
                    onChange={handleFormChange}
                    placeholder="Delivery Address"
                    className="w-full px-3 py-2 border rounded-md"
                  />
                  <input
                    type="date"
                    name="date"
                    value={formData.date}
                    onChange={handleFormChange}
                    className="w-full px-3 py-2 border rounded-md"
                  />
                  <button
                    type="submit"
                    className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-xl"
                  >
                    Submit Order
                  </button>
                </form>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
