import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Users = () => {
  const [orders, setOrders] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (user) {
      axios
        .get(`http://localhost:3000/api/orders/user/${user._id}`)
        .then((res) => setOrders(res.data))
        .catch((err) => console.error('Failed to fetch orders:', err));
    }
  }, [user]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-100 p-6">
      <h1 className="text-4xl font-bold text-center mb-8 text-purple-800">ðŸ“¦ My Orders</h1>

      {orders.length === 0 ? (
        <p className="text-center text-lg text-gray-600">You havenâ€™t ordered anything yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {orders.map((order) => (
            <div key={order._id} className="bg-white shadow-xl p-5 rounded-xl">
              <img
                src={order.product?.image || '/placeholder.jpg'}
                alt={order.product?.name}
                className="w-full h-40 object-cover rounded-lg"
              />
              <h2 className="text-xl font-bold mt-4">{order.product?.name}</h2>
              <p className="text-green-600 font-medium">â‚¹{order.product?.price}</p>
              <p className="text-sm text-gray-500 mt-1">Ordered on: {new Date(order.createdAt).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Users;
