import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      setName(user.name || '');
      setPhone(user.phone || '');
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      alert("‚ö†Ô∏è Please login to place an order.");
      return navigate('/login');
    }

    if (cart.length === 0) {
      alert("‚ö†Ô∏è Your cart is empty!");
      return;
    }

    if (!phone.match(/^\d{10}$/)) {
      alert("‚ö†Ô∏è Enter a valid 10-digit phone number.");
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        userId: user._id,
        name: name.trim(),
        address: address.trim(),
        phone: phone.trim(),
        items: cart,
        total,
      };

      const response = await axios.post('http://localhost:5000/api/orders', orderData, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
      });

      if (response.status === 201 || response.status === 200) {
        alert("‚úÖ Order placed successfully!");
        clearCart();
        navigate('/thankyou');
      } else {
        alert("‚ùå Order failed. Please try again.");
      }
    } catch (error) {
      console.error("üö® Order Error:", error.response?.data || error.message);
      alert(
        error?.response?.data?.error ||
        error?.response?.data?.message ||
        "‚ùå Server error while placing order."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-center mb-6">üßæ Checkout</h1>
      <div className="grid md:grid-cols-2 gap-6">
        {/* Checkout Form */}
        <form onSubmit={handleSubmit} className="space-y-4 bg-white p-4 shadow rounded">
          <input
            type="text"
            placeholder="Name"
            className="border p-2 w-full"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="tel"
            placeholder="Phone"
            className="border p-2 w-full"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
          <textarea
            placeholder="Address"
            className="border p-2 w-full"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
          <button
            type="submit"
            disabled={loading}
            className={`w-full px-4 py-2 rounded text-white ${loading ? 'bg-gray-400' : 'bg-green-600 hover:bg-green-700'}`}
          >
            {loading ? 'Placing Order...' : '‚úÖ Place Order'}
          </button>
        </form>

        {/* Order Summary */}
        <div className="bg-white p-4 border rounded shadow">
          <h2 className="text-xl font-semibold mb-4">üõç Order Summary</h2>
          {cart.length > 0 ? (
            <>
              <ul className="space-y-2">
                {cart.map((item, i) => (
                  <li key={i} className="border-b pb-2">
                    <p className="font-medium">{item.name}</p>
                    <p className="text-sm text-gray-600">
                      Qty: {item.quantity} √ó ‚Çπ{item.price}
                    </p>
                    <p className="text-sm font-semibold text-black">
                      Subtotal: ‚Çπ{item.price * item.quantity}
                    </p>
                  </li>
                ))}
              </ul>
              <hr className="my-4" />
              <p className="text-lg font-bold">Total: ‚Çπ{total}</p>
            </>
          ) : (
            <p className="text-red-500">Your cart is empty.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Checkout;
