import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');

  useEffect(() => {
    const cartData = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(cartData);
  }, []);

  const removeFromCart = (productId) => {
    const updatedCart = cartItems.filter(item => item._id !== productId);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    setCartItems(updatedCart);
  };

  const getTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price, 0);
  };

  const placeOrder = async (e) => {
    e.preventDefault();

    if (cartItems.length === 0) {
      alert("Cart is empty. Add items to place an order.");
      return;
    }

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user._id) {
      alert("You must be logged in to place an order.");
      return;
    }

    if (!name || !phone || !address) {
      alert("Please fill out all fields before placing the order.");
      return;
    }

    const total = getTotal();

    const orderPayload = {
      userId: user._id,
      name,
      phone,
      address,
      items: cartItems.map(item => ({
        productId: item._id,
        name: item.name,
        price: item.price,
        quantity: 1
      })),
      total
    };

    try {
      await axios.post('http://localhost:5000/api/orders', orderPayload);
      alert('✅ Order placed successfully!');
      localStorage.removeItem('cart');
      setCartItems([]);
      setName('');
      setPhone('');
      setAddress('');
    } catch (error) {
      console.error('❌ Order error:', error.response?.data || error.message);
      alert('❌ Failed to place order. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 via-pink-200 to-yellow-100 p-6">
      <div className="text-center mb-8">
        <h1 className="text-5xl font-extrabold text-pink-800 tracking-tight animate-pulse mb-2">Your Shopping Cart</h1>
        <p className="text-lg text-gray-700 italic">All your selected luxury miniature cars in one place.</p>
      </div>

      {cartItems.length === 0 ? (
        <p className="text-center text-lg text-gray-700 mt-10">Your cart is empty. Start shopping now!</p>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
            {cartItems.map((item) => (
              <div key={item._id} className="bg-white rounded-2xl shadow-xl p-5 transform hover:scale-105 transition duration-300 ease-in-out">
                <img src={item.image} alt={item.name} className="h-48 w-full object-cover rounded-xl shadow-sm" />
                <h2 className="text-xl font-bold mt-4 text-gray-800">{item.name}</h2>
                <p className="text-green-600 font-semibold mt-1">₹{item.price}</p>
                <p className="text-yellow-500 text-sm">⭐ {item.rating} / 5</p>
                <button
                  onClick={() => removeFromCart(item._id)}
                  className="mt-4 bg-red-500 hover:bg-red-600 text-white w-full py-2 rounded-xl transition-all duration-200"
                >
                  Remove
                </button>
              </div>
            ))}
          </div>

          <div className="mt-12 text-center">
            <h2 className="text-3xl font-bold text-pink-800">Total: ₹{getTotal()}</h2>
            <p className="text-gray-600 mt-2 mb-6">Taxes and delivery calculated at checkout.</p>

            <form onSubmit={placeOrder} className="max-w-md mx-auto bg-white p-6 rounded-xl shadow-md space-y-4">
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Your Full Name"
                className="w-full px-4 py-2 border rounded-lg"
              />
              <input
                type="tel"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                placeholder="Phone Number"
                className="w-full px-4 py-2 border rounded-lg"
              />
              <textarea
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Delivery Address"
                className="w-full px-4 py-2 border rounded-lg"
              ></textarea>

              <button
                type="submit"
                className="w-full bg-green-600 hover:bg-green-700 text-white py-3 rounded-xl font-semibold text-lg"
              >
                Place Order
              </button>
            </form>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
