import React from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  const buttons = [
    { name: "Users", route: "/users" },
    { name: "Products", route: "/products" },
    { name: "Orders", route: "/orders" },
    { name: "Admin", route: "/admin-login" },
  ];

  return (
    <div className="min-h-screen relative bg-gradient-to-bl from-[#0f0c29] via-[#302b63] to-[#24243e] font-[Orbitron] overflow-hidden">
      {/* Background Video */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute top-0 left-0 w-full h-full object-cover z-[-1] opacity-20"
      >
        <source src="/video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-6 text-center backdrop-blur-sm">
        <h1 className="text-5xl text-white font-bold mb-12 animate-pulse drop-shadow-[0_0_10px_rgba(255,255,255,0.5)]">
          âš¡ Control Center
        </h1>

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-xl">
          {buttons.map(({ name, route }) => (
            <button
              key={name}
              onClick={() => navigate(route)}
              className="p-6 w-full text-white text-xl font-semibold rounded-xl 
                         bg-white/10 backdrop-blur-xl border border-white/30 
                         hover:border-pink-400 hover:shadow-[0_0_25px_rgba(255,255,255,0.3)] 
                         transform transition-all duration-300 hover:scale-105"
            >
              <span className="block bg-gradient-to-r from-pink-400 via-purple-400 to-blue-500 bg-clip-text text-transparent">
                {name}
              </span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
