import React from "react";
import { useNavigate } from "react-router-dom";
import {
  UserIcon,
  CubeIcon,
  ClipboardDocumentIcon,
  ShieldCheckIcon
} from "@heroicons/react/24/outline";

const Dashboard = () => {
  const navigate = useNavigate();

  const buttons = [
    { name: "Users", path: "/users", icon: <UserIcon className="h-6 w-6" /> },
    { name: "Products", path: "/products", icon: <CubeIcon className="h-6 w-6" /> },
    { name: "Orders", path: "/orders", icon: <ClipboardDocumentIcon className="h-6 w-6" /> },
    { name: "Admin", path: "/admin-login", icon: <ShieldCheckIcon className="h-6 w-6" /> },
  ];

  return (
    <div className="min-h-screen relative bg-gradient-to-br from-[#0f0c29] via-[#302b63] to-[#24243e] font-[Orbitron] overflow-hidden">
      {/* Background Video */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute top-0 left-0 w-full h-full object-cover z-[-1] opacity-20"
      >
        <source src="/video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content Overlay */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-6 text-center backdrop-blur-md">
        <h1 className="text-5xl text-white font-bold mb-12 animate-pulse drop-shadow-[0_0_10px_rgba(255,255,255,0.6)]">
          âš¡ Control Center
        </h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-xl">
          {buttons.map(({ name, path, icon }) => (
            <button
              key={name}
              onClick={() => navigate(path)}
              className="flex items-center gap-3 justify-center p-5 text-white text-lg font-semibold rounded-xl 
                         bg-white/10 backdrop-blur-lg border border-white/30 shadow-xl 
                         hover:scale-105 hover:shadow-[0_0_25px_rgba(255,255,255,0.3)] 
                         transition-all duration-300"
            >
              {icon}
              {name}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
