import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminUsers = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/orders/admin/orders')
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  const grouped = data.reduce((acc, order) => {
    const { userId, productId } = order;
    if (!acc[userId._id]) {
      acc[userId._id] = { name: userId.name, email: userId.email, orders: [] };
    }
    acc[userId._id].orders.push({ name: productId.name, price: productId.price });
    return acc;
  }, {});

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">All Users & Their Orders</h2>
      {Object.values(grouped).map((user, i) => (
        <div key={i} className="mb-6 bg-white p-4 rounded shadow">
          <p className="font-semibold">{user.name} ({user.email})</p>
          <table className="table-auto w-full mt-2 border">
            <thead>
              <tr>
                <th className="border px-2">Product</th>
                <th className="border px-2">Price</th>
              </tr>
            </thead>
            <tbody>
              {user.orders.map((order, idx) => (
                <tr key={idx}>
                  <td className="border px-2">{order.name}</td>
                  <td className="border px-2">â‚¹{order.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default AdminUsers;
