import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem('user'));

    if (!user) {
      alert("Please login to place an order.");
      return;
    }

    try {
      // Send one combined order with multiple products
      const response = await axios.post('http://localhost:5000/api/orders', {
        userId: user._id,
        name,
        address,
        items: cart,
        total,
      }, {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      });

      if (response.status === 201 || response.status === 200) {
        alert("✅ Order placed successfully!");
        clearCart();
        navigate('/thankyou');
      } else {
        alert("❌ Failed to place order.");
      }
    } catch (error) {
      console.error("Order Error:", error);
      alert("❌ Server error while placing order.");
    }
  };

  return (
    <div className="p-6 bg-purple-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">Checkout</h1>
      <div className="mb-4">
        <p className="text-lg font-medium text-gray-700">Total: ₹{total}</p>
        <p className="text-sm text-gray-500">({cart.length} item{cart.length !== 1 ? 's' : ''})</p>
      </div>
      <form onSubmit={handleSubmit} className="bg-white p-4 rounded-xl shadow-md space-y-4 max-w-md">
        <input
          type="text"
          placeholder="Full Name"
          className="border w-full p-2 rounded"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <textarea
          placeholder="Address"
          className="border w-full p-2 rounded"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded"
        >
          Place Order
        </button>
      </form>
    </div>
  );
};

export default Checkout;
