import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="p-6 bg-gradient-to-br from-pink-100 to-blue-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center">üõçÔ∏è Shopping Page</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {products.map(p => (
          <div key={p._id} className="bg-white p-4 rounded-xl shadow-md text-center">
            <img src={p.image} alt={p.name} className="w-full h-48 object-cover rounded mb-2" />
            <h2 className="text-lg font-bold">{p.name}</h2>
            <p className="text-green-600 font-semibold">‚Çπ{p.price}</p>
            <p className="text-red-500">{p.offer}</p>
            <div className="flex justify-center gap-2 mt-2">
              <button className="bg-blue-500 text-white px-4 py-1 rounded">Add to Cart</button>
              <button className="bg-green-500 text-white px-4 py-1 rounded">Buy Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
