import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/orders') // No need for /admin/orders if your GET /orders is global
      .then(res => setOrders(res.data))
      .catch(err => console.error('Error fetching orders:', err));
  }, []);

  // Group orders by product
  const grouped = {};

  orders.forEach((order) => {
    if (!order.items || !Array.isArray(order.items) || !order.userId) return;

    order.items.forEach((item) => {
      const productId = item._id || item.productId || item.name;
      if (!productId) return;

      if (!grouped[productId]) {
        grouped[productId] = {
          name: item.name,
          users: new Set(), // Use Set to avoid duplicates
          rawUsers: []
        };
      }

      const userLabel = `${order.name || 'Unknown'} (${order.phone || 'No Phone'})`;

      if (!grouped[productId].users.has(userLabel)) {
        grouped[productId].users.add(userLabel);
        grouped[productId].rawUsers.push(userLabel);
      }
    });
  });

  return (
    <div className="p-6 min-h-screen bg-gradient-to-br from-blue-50 to-purple-100">
      <h2 className="text-2xl font-bold text-purple-700 mb-6 text-center">ðŸ“¦ Product-wise Order Summary</h2>

      {Object.values(grouped).length === 0 ? (
        <p className="text-center text-gray-600">No orders found.</p>
      ) : (
        Object.values(grouped).map((product, index) => (
          <div key={index} className="mb-6 bg-white p-6 rounded-xl shadow-md">
            <p className="text-lg font-semibold text-purple-800 mb-2">
              {product.name} â€” Ordered by {product.rawUsers.length} user(s)
            </p>
            <ul className="list-disc list-inside text-gray-700">
              {product.rawUsers.map((user, idx) => (
                <li key={idx}>{user}</li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminOrders;
