import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminOrders = () => {
  const [productOrders, setProductOrders] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/admin/product-orders')
      .then(res => setProductOrders(res.data))
      .catch(err => console.error('Failed to fetch orders:', err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Orders Per Product</h1>
      <table className="min-w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="border px-4 py-2">Product Name</th>
            <th className="border px-4 py-2">Total Orders</th>
            <th className="border px-4 py-2">Users Who Ordered</th>
          </tr>
        </thead>
        <tbody>
          {productOrders.map((product, index) => (
            <tr key={index} className="border-t hover:bg-gray-50">
              <td className="border px-4 py-2">{product.productName}</td>
              <td className="border px-4 py-2">{product.totalOrders}</td>
              <td className="border px-4 py-2">
                <ul className="list-disc ml-4">
                  {product.users.map((user, i) => (
                    <li key={i}>{user.name} â€“ {user.email}</li>
                  ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminOrders;
