import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) return alert("Please login to place an order.");

    if (!/^\d{10}$/.test(phone)) {
      return alert("üìû Please enter a valid 10-digit phone number.");
    }

    const orderItems = cart.map(item => ({
      productId: item._id || item.productId || '', // fallback if key is missing
      name: item.name,
      price: item.price,
      quantity: item.quantity
    }));

    const payload = {
      userId: user._id,
      name,
      address,
      phone,
      items: orderItems,
      total
    };

    console.log("üßæ Order Payload:", payload); // Debug log

    try {
      const response = await axios.post('http://localhost:5000/api/orders', payload, {
        headers: {
          Authorization: `Bearer ${user.token}`
        }
      });

      if (response.status === 201 || response.status === 200) {
        alert("‚úÖ Order placed successfully!");
        clearCart();
        navigate('/thankyou');
      } else {
        alert("‚ùå Failed to place order.");
      }
    } catch (error) {
      console.error("‚ùå Order Error:", error.response?.data || error.message);
      alert(`‚ùå Server error: ${error.response?.data?.message || 'Unable to place order.'}`);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-center mb-6">üßæ Checkout</h1>
      <div className="grid md:grid-cols-2 gap-6">
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Name"
            className="border p-2 w-full"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="tel"
            placeholder="Phone"
            className="border p-2 w-full"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
          <textarea
            placeholder="Address"
            className="border p-2 w-full"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
          <button type="submit" className="bg-purple-600 text-white px-4 py-2 rounded">
            Place Order
          </button>
        </form>

        <div className="bg-white p-4 border rounded shadow">
          <h2 className="text-xl font-semibold mb-2">üõç Order Summary</h2>
          {cart.map((item, i) => (
            <div key={i} className="mb-2">
              <p className="font-medium">{item.name}</p>
              <p className="text-sm text-gray-600">
                Qty: {item.quantity} √ó ‚Çπ{item.price}
              </p>
            </div>
          ))}
          <p className="mt-4 font-bold">Total: ‚Çπ{total}</p>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
