// src/pages/Signup.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const navigate = useNavigate();

  // Redirect if already logged in
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      if (user.role === 'admin') navigate('/admin');
      else navigate('/dashboard');
    }
  }, [navigate]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/users/signup', form);
      localStorage.setItem('user', JSON.stringify(res.data));
      if (res.data.role === 'admin') navigate('/admin');
      else navigate('/dashboard');
    } catch (err) {
      alert("Signup failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-100 to-purple-200">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-xl w-96 space-y-4">
        <h2 className="text-2xl font-bold text-purple-800 text-center">Sign Up</h2>
        <input type="text" name="name" placeholder="Name" onChange={handleChange} required
               className="w-full px-4 py-2 border rounded-md" />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required
               className="w-full px-4 py-2 border rounded-md" />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required
               className="w-full px-4 py-2 border rounded-md" />
        <button type="submit" className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-700">
          Sign Up
        </button>
        <p className="text-center text-sm">Already have an account? <a href="/login" className="text-purple-700">Login</a></p>
      </form>
    </div>
  );
};

export default Signup;
