import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Users = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/users/with-orders')
      .then(res => setUsers(res.data))
      .catch(err => console.error("Failed to load users:", err));
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-blue-100 p-6">
      <h1 className="text-4xl font-extrabold text-center text-indigo-800 mb-10 drop-shadow">
        üë• All Users & Their Orders
      </h1>

      <div className="space-y-10">
        {users.map(user => (
          <div key={user._id} className="bg-white shadow-md rounded-2xl p-6">
            <div className="mb-4 border-b pb-2">
              <h2 className="text-2xl font-semibold text-blue-700">
                {user.name}
                <span className="text-gray-500 text-sm font-normal ml-2">({user.email})</span>
              </h2>
              <p className="text-sm text-gray-500">Role: {user.role}</p>
            </div>

            {user.orders.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-5">
                {user.orders.map(order => (
                  <div
                    key={order._id}
                    className="bg-white/90 border rounded-xl p-4 shadow hover:shadow-lg transition"
                  >
                    <img
                      src={order.product?.image}
                      alt={order.product?.name}
                      className="w-full h-40 object-cover rounded-md mb-3"
                    />
                    <p className="font-bold text-gray-800 truncate">{order.product?.name}</p>
                    <p className="text-sm text-green-700 font-medium">‚Çπ{order.product?.price}</p>
                    <p className="text-sm text-yellow-500">‚≠ê {order.product?.rating}</p>
                    <p className="text-xs text-gray-500 mt-1">
                      Ordered on: {new Date(order.date).toLocaleDateString()}
                    </p>
                    <Link
                      to={`/products/${order.product?._id}`}
                      className="inline-block mt-3 text-sm text-blue-600 hover:underline"
                    >
                      üîé View Product
                    </Link>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 italic mt-2">No orders placed.</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Users;
