// src/pages/Login.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  // Redirect if already logged in
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      if (user.role === 'admin') navigate('/admin');
      else navigate('/dashboard');
    }
  }, [navigate]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/users/login', form);
      localStorage.setItem('user', JSON.stringify(res.data));
      if (res.data.role === 'admin') navigate('/admin');
      else navigate('/dashboard');
    } catch (err) {
      alert("Login failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-100 to-pink-100">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-lg w-96 space-y-4">
        <h2 className="text-2xl font-bold text-purple-800 text-center">Login</h2>
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required
               className="w-full px-4 py-2 border rounded-md" />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required
               className="w-full px-4 py-2 border rounded-md" />
        <button type="submit" className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-700">
          Login
        </button>
        <p className="text-center text-sm">Don't have an account? <a href="/signup" className="text-purple-700">Sign Up</a></p>
      </form>
    </div>
  );
};

export default Login;
