import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const location = useLocation(); // updates when you navigate to /cart

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(storedCart);  // ✅ loads cart items
  }, [location.pathname]);

  const removeFromCart = (id) => {
    const updatedCart = cartItems.filter(item => item._id !== id);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const clearCart = () => {
    localStorage.removeItem("cart");
    setCartItems([]);
  };

  const total = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-100 p-6">
      <h1 className="text-5xl font-bold text-center text-purple-700 mb-8">Your Cart</h1>

      {cartItems.length ? (
        <>
          <div className="flex justify-end mb-4">
            <button
              onClick={clearCart}
              className="bg-red-500 hover:bg-red-600 text-white px-6 py-2 rounded-xl transition"
            >
              Clear Cart
            </button>
          </div>

          <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 md:grid-cols-3">
            {cartItems.map((item) => (
              <div key={item._id} className="bg-white shadow-md rounded-xl overflow-hidden flex flex-col">
                <img src={item.image} alt={item.name} className="w-full h-48 object-cover" />
                <div className="p-4 flex flex-col justify-between flex-1">
                  <div>
                    <h2 className="text-xl font-semibold text-gray-800">{item.name}</h2>
                    <p className="text-green-600 font-medium">₹{item.price}</p>
                  </div>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    className="mt-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-xl"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="mt-10 text-center">
            <div className="text-2xl font-bold text-purple-800 mb-4">
              Total: ₹{total}
            </div>
            <button
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-xl text-xl font-bold"
              onClick={() => alert("Checkout feature coming soon!")}
            >
              Proceed to Checkout
            </button>
          </div>
        </>
      ) : (
        <p className="text-center text-gray-600 text-xl mt-20">Your cart is empty.</p>
      )}
    </div>
  );
};

export default Cart;
