import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/users/signup', form);
      alert("Signup successful");
      navigate('/');
    } catch (err) {
      alert("Signup failed");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-100 to-purple-200 flex items-center justify-center">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-md w-96 space-y-4">
        <h2 className="text-2xl font-bold text-purple-800 text-center">Create Account</h2>
        <input type="text" name="name" placeholder="Name" onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md" required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md" required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md" required />
        <button type="submit" className="w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700">
          Sign Up
        </button>
        <p className="text-center text-sm">
          Already have an account? <a href="/" className="text-purple-700 font-semibold">Login</a>
        </p>
      </form>
    </div>
  );
};

export default Signup;
