import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminUsers = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/admin/users-with-orders')
      .then(res => setUsers(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">All Users and Their Orders</h1>
      {users.map((user, i) => (
        <div key={i} className="bg-white shadow rounded p-4 mb-4">
          <h2 className="text-xl font-semibold">{user.name} ({user.email})</h2>
          <ul className="list-disc list-inside ml-4">
            {user.productsOrdered.map((product, j) => (
              <li key={j}>{product.name}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default AdminUsers;
