import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/users/login', form);
      const { role } = res.data;

      // Save token or user if needed
      localStorage.setItem('user', JSON.stringify(res.data));

      if (role === 'admin') {
        navigate('/admin');
      } else {
        navigate('/dashboard');
      }
    } catch (err) {
      alert("Invalid login");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-100 to-pink-100 flex items-center justify-center">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-xl shadow-md w-96 space-y-4">
        <h2 className="text-2xl font-bold text-purple-800 text-center">Login</h2>
        <input type="email" name="email" placeholder="Email" onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md" required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange}
          className="w-full px-4 py-2 border rounded-md" required />
        <button type="submit" className="w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700">
          Login
        </button>
        <p className="text-center text-sm">
          Don't have an account? <a href="/signup" className="text-purple-700 font-semibold">Sign Up</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
